import{e as t,o as n,y as e}from"./vendor.340d19f5.js";const o={class:"markdown-body"},d=[e('<h1>DollMachine 抓娃娃</h1><h3>介绍</h3><p>模拟娃娃机抽奖，可配置图片、开始结束回调等。</p><h3>安装</h3><pre><code class="language-javascript">import { createApp } from &#39;vue&#39;;\n// vue\nimport { Dollmachine } from &#39;@nutui/nutui-bingo&#39;;\n\nconst app = createApp();\napp.use(Dollmachine);\n</code></pre><h2>代码示例</h2><h2>基本用法</h2><pre><code class="language-html">&lt;nutbig-doll-machine\n  ref=&quot;lottoRollDom&quot;\n  :prize-list=&quot;prizeList&quot;\n  :prize-index=&quot;prizeIndex&quot;\n  @start-turns=&quot;startTurns&quot;\n  @end-turns=&quot;endTurns&quot;\n&gt;\n&lt;/nutbig-doll-machine&gt;\n</code></pre><pre><code class="language-javascript">export default {\n  props: {},\n  setup() {\n    const lottoRollDom:any = ref(null);\n    const prizeList = reactive([\n      {\n        imagePath: &quot;https://img11.360buyimg.com/imagetools/jfs/t1/147182/12/2440/6194/5f06cde6Ead240fe8/31082e30a182a5ce.png&quot;,\n        text: &quot;大鸡腿&quot;\n      },\n      {\n        imagePath: &quot;https://img12.360buyimg.com/imagetools/jfs/t1/221361/4/7410/16458/61c9261eE45802396/27b64caa9e7c9bac.png&quot;,\n        text: &quot;JOY&quot;\n      },\n      { \n        imagePath: &quot;https://img11.360buyimg.com/imagetools/jfs/t1/128607/26/6643/6790/5f06cd27E9b5e15f7/7509bc7ce2da66b8.png&quot;,\n        text: &quot;VIP&quot;\n      },\n      {\n        imagePath: &quot;https://img12.360buyimg.com/imagetools/jfs/t1/221361/4/7410/16458/61c9261eE45802396/27b64caa9e7c9bac.png&quot;,\n        text: &quot;JOY&quot;\n      },\n    ]);\n\n    // 中奖的奖品的index(此数据可根据后台返回的值重新赋值)\n    const prizeIndex = ref(3); \n    const startTurns = () =&gt; {\n      console.log(&#39;开始抽奖&#39;);\n    }\n\n    const endTurns = () =&gt; {\n      console.log(&#39;抽奖结束&#39;);\n      setTimeout(() =&gt; {\n        Dialog({\n          title: &#39;中奖提示&#39;,\n          content: &#39;您已完成抽奖，是否继续？&#39;,\n          onCancel: () =&gt; {},\n          onOk: () =&gt; {\n            setTimeout(() =&gt; {\n              lottoRollDom.value.init();\n            }, 300);\n          }\n        });\n      }, 5000);\n    }\n   \n    return {\n      lottoRollDom,\n      prizeList,\n      prizeIndex,\n      startTurns,\n      endTurns\n    };\n  }\n}\n</code></pre><h3>Props</h3><table><thead><tr><th>字段</th><th>说明</th><th>类型</th><th>默认值</th></tr></thead><tbody><tr><td>prize-list</td><td>奖品列表</td><td>Array</td><td>目前需要至少4个奖品</td></tr><tr><td>speed</td><td>奖品滚动的速度</td><td>Number</td><td>20</td></tr><tr><td>init</td><td>初始化游戏</td><td>Function</td><td>-</td></tr><tr><td>prize-index</td><td>中奖奖品在列表的索引位置</td><td>Number</td><td>-1（-1未中奖）</td></tr></tbody></table><h3>Events</h3><table><thead><tr><th>字段</th><th>说明</th><th>回调参数</th></tr></thead><tbody><tr><td>init</td><td>游戏初始化</td><td>dom.value.init()</td></tr><tr><td>start-turns</td><td>爪子开始下伸，赋值到 prize-index</td><td>-</td></tr><tr><td>end-turns</td><td>爪子已经抓到/未抓到奖品触发</td><td>-</td></tr></tbody></table>',13)],r={setup:e=>(e,r)=>(n(),t("div",o,d))};export{r as default};
